%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( time, intensity );

% Set up fittype and options.
ft = fittype( 'exp2' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.Normalize = 'on';
opts.StartPoint = [0.00103500247510036 -4.83099122557793 1.01931771026908 -0.0612769854350009];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
figure( 'Name', 'untitled fit 1' );
